pragma solidity ^0.8.0;

contract Bet {
    address private immutable owner;
    uint256 public immutable entryFee;

    // For each match (id 1-5), store the winning team/option (set by the owner)
    mapping(uint256 => uint256) public matchWinner;

    // For each user and match, track if the user has entered that match
    mapping(address => mapping(uint256 => bool)) public hasEntered;

    // For each user and match, track the amount bet
    mapping(address => mapping(uint256 => uint256)) public gamblersToAmountBet;

    // For each user and match, track the team (or option) they bet on
    mapping(address => mapping(uint256 => uint256)) public BetOn;

    // Overall total deposited amount per user (cumulative)
    mapping(address => uint256) public TotalAmount;

    // For each match, maintain an array of addresses that have placed a bet
    mapping(uint256 => address[]) public matchGamblers;

    // (Optional) Mapping for winning amounts (if you wish to track them separately)
    mapping(address => uint256) public WinningAmount;

    event UserEntered(address user, uint256 matchId, uint256 amount);
    event UserWon(address user, uint256 matchId, uint256 amount);
    event UserLost(address user, uint256 matchId, uint256 amount);

    // New definitions for bet history—each record now holds the match id instead of a game id.
    enum BetResult { Loss, Win }

    struct BetRecord {
        uint256 matchId;
        BetResult result;
        uint256 amountWon;
        uint256 amountBet; // Extra amount earned (if any) for a win
        uint256 timestamp;
    }

    mapping(address => BetRecord[]) public betHistory;

    constructor(uint256 _entryFee) {
        owner = msg.sender;
        entryFee = _entryFee;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    /**
     * @notice Allows a user to enter a bet for a specific match (matchId should be 1, 2, 3, 4, or 5).
     * @param matchId The match identifier.
     * @param Bet_On The team or option the user is betting on.
     */
    function enter(uint256 matchId, uint256 Bet_On) public payable {
        require(msg.value >= (entryFee / 10 ** 18), "Insufficient entry fee");
        require(!hasEntered[msg.sender][matchId], "You have already entered this match");
        gamblersToAmountBet[msg.sender][matchId] = msg.value;
        BetOn[msg.sender][matchId] = Bet_On;
        matchGamblers[matchId].push(msg.sender);
        hasEntered[msg.sender][matchId] = true;
        emit UserEntered(msg.sender, matchId, msg.value);
        TotalAmount[msg.sender] += msg.value;
    }

    /**
     * @notice Settles a winning bet for a given match.
     *         This function can only be called if the user bet on the winning team.
     * @param matchId The match identifier.
     */
    function settleTeamResultWon(uint256 matchId) public payable {
        require(hasEntered[msg.sender][matchId], "User has not entered this match");
        require(BetOn[msg.sender][matchId] == matchWinner[matchId], "You did not bet on the winning team");

        uint256 losingPool = 0;
        uint256 winnersCount = 0;
        address[] memory gamblers = matchGamblers[matchId];
        uint256 totalWinnerBet = 0;
        // Calculate the total bet from users who lost and count winners
        for (uint256 i = 0; i < gamblers.length; i++) {
            if (BetOn[gamblers[i]][matchId] == matchWinner[matchId]) {
                 totalWinnerBet=gamblersToAmountBet[gamblers[i]][matchId];
                winnersCount++;
            } else {
                losingPool += gamblersToAmountBet[gamblers[i]][matchId];
            }
        }
        require(winnersCount > 0, "No winners found");

        // Distribute the losing pool equally among winners
         uint256 userBet = gamblersToAmountBet[msg.sender][matchId];
        uint256 additionalWinnings = (losingPool * userBet) / totalWinnerBet;
        uint256 totalWinnings = gamblersToAmountBet[msg.sender][matchId] + additionalWinnings;
        // Transfer the extra winnings (the original bet remains in the contract)
        payable(msg.sender).transfer(totalWinnings);

        // Mark the user's bet as settled for this match
        hasEntered[msg.sender][matchId] = false;

        // Record the winning bet details in the user's bet history
        betHistory[msg.sender].push(
            BetRecord({
                matchId: matchId,
                result: BetResult.Win,
                amountWon: additionalWinnings,
                amountBet:userBet,
                timestamp: block.timestamp
            })
        );

        emit UserWon(msg.sender, matchId, totalWinnings);
    }

    /**
     * @notice Settles a lost bet for a given match.
     *         This function can only be called if the user bet on the losing team.
     * @param matchId The match identifier.
     */
    function settleTeamResultLoss(uint256 matchId) public {
        require(hasEntered[msg.sender][matchId], "User has not entered this match");
        require(BetOn[msg.sender][matchId] != matchWinner[matchId], "Your team won; cannot settle as loss");

        // Mark the user's bet as settled for this match
        hasEntered[msg.sender][matchId] = false;

        // Record the lost bet details in the user's bet history
        betHistory[msg.sender].push(
            BetRecord({
                matchId: matchId,
                result: BetResult.Loss,
                amountBet:gamblersToAmountBet[msg.sender][matchId],
                amountWon: 0,
                timestamp: block.timestamp
            })
        );

        emit UserLost(msg.sender, matchId, entryFee);
    }

    /**
     * @notice Allows the owner to withdraw the entire contract balance.
     */
    function withdraw() public payable onlyOwner {
        payable(owner).transfer(address(this).balance);
    }

    function getOwner() public view returns (address) {
        return owner;
    }

    function getEntryFee() public view returns (uint256) {
        return entryFee;
    }

    /**
     * @notice Returns the overall deposited amount for a given user (across all matches).
     * @param user The user's address.
     */
    function getTotalDeposited(address user) public view returns (uint256) {
        return TotalAmount[user];
    }

    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @notice Allows the owner to set the winning team for a specific match.
     * @param matchId The match identifier.
     * @param win The winning team/option.
     */
    function setWinner(uint256 matchId, uint256 win) public onlyOwner {
        matchWinner[matchId] = win;
    }

    /**
     * @notice Retrieves a user's bet history.
     * @param user The user's address.
     * @return matchIds Array of match identifiers.
     * @return results Array of results (0 for Loss, 1 for Win).
     * @return amountsWon Array of extra amounts won.
     * @return amountBet Array of the bet amount for each match (if any).
     * @return timestamps Array of settlement timestamps.
     */
    function getBetHistory(address user)
        public
        view
        returns (
            uint256[] memory matchIds,
            uint256[] memory results,
            uint256[] memory amountsWon,
            uint256[] memory amountBet,
            uint256[] memory timestamps
        )
    {
        uint256 len = betHistory[user].length;
        matchIds = new uint256[](len);
        results = new uint256[](len);
        amountsWon = new uint256[](len);
        amountBet = new uint256[](len);
        timestamps = new uint256[](len);

        for (uint256 i = 0; i < len; i++) {
            BetRecord memory record = betHistory[user][i];
            matchIds[i] = record.matchId;
            results[i] = uint256(record.result);
            amountsWon[i] = record.amountWon;
            amountBet[i]=record.amountBet;
            timestamps[i] = record.timestamp;
        }
    }
}
